package loopTest1216;

public class Exam2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("----------------------");
		System.out.println("5의 배수 출력 프로그램 1.0");
		System.out.println("----------------------");
		for(int i=1; i<=100; i++) {
			if(i % 5 == 0) {
			System.out.print(i + " ");
			}
		}

	}

}
